import React, { Component } from "react";
import { TextInput, View, Text, FlatList, TouchableOpacity, StyleSheet } from "react-native";
import { auth, db } from "../../firebase/config";

class Search extends Component {
  constructor() {
    super();
    this.state = {
      search: "",
      resultsUserName: [],
      resultsEmail: [],
      results: [],
      hayResultados: false,
      allUsers: [],
      selectedUserId: "",
    };
  }

  componentDidMount() {
    db.collection("user").onSnapshot((data) => {
      let info = [];
      data.forEach((i) => {
        info.push({
          id: i.id,
          datos: i.data(),
        });
      });

      this.setState({
        allUsers: info,
      });
    });
  }

  search(event) {
    this.setState({ search: event.target.value });

    db.collection("user")
      .where("owner", "==", this.state.search)
      .get()
      .then((resultOwner) => {
        let resultUsers = [];

        resultOwner.forEach((owner) => {
          resultUsers.push({
            id: owner.id,
            datos: owner.data(),
          });
        });

        this.setState({
          resultsEmail: resultUsers,
          hayResultados: true,
        });
      })
      .catch((e) => console.log(e));

    db.collection("user")
      .where("userName", "==", this.state.search)
      .get()
      .then((resultUserName) => {
        let resultUsers = [];

        resultUserName.forEach((userName) => {
          resultUsers.push({
            id: userName.id,
            datos: userName.data(),
          });
        });

        this.setState({
          resultsUserName: resultUsers,
          hayResultados: true,
        });
      })
      .catch((e) => console.log(e));
  }

  handleUserSelect(selectedUserId) {
    // Cargar el usuario seleccionado
    this.setState({
      selectedUserId,
    });
  }

  filtro(event) {
    this.setState({ search: event.target.value });
    let filtroResults = [];
    this.state.allUsers.forEach((user) => {
      console.log(user);
      if (user.datos.email.includes(this.state.search)) {
        filtroResults.push({
          id: user.id,
          datos: user.datos,
        });
      }
    });
    this.setState({
      results: filtroResults,
    });
  }

  render() {
    const { search, results } = this.state;

    // Filtrar usuarios basados en la entrada del usuario
    const filteredResults = results.filter((user) =>
      user.datos.userName.toLowerCase().includes(search.toLowerCase())
    );

    return (
      <View>
        <TextInput
          placeholder="Search by username or email"
          keyboardType="default"
          value={search}
          style={styles.input}
          onChange={(event) => this.search(event)}
        />

        {this.state.hayResultados ? (
          <Text>Couldn't find what you are searching for</Text>
        ) : this.state.resultsUserName.length > 0 ? (
          <FlatList
            data={this.state.resultsUserName}
            keyExtractor={(user) => user.id}
            renderItem={({ item }) => (
              <TouchableOpacity onPress={() => this.handleUserSelect(item.id)}>
                <View>
                  <Text>{item.datos.userName}</Text>
                </View>
              </TouchableOpacity>
            )}
          />
        ) : (
          <FlatList
            data={this.state.resultsEmail}
            keyExtractor={(user) => user.id}
            renderItem={({ item }) => (
              <TouchableOpacity onPress={() => this.handleUserSelect(item.id)}>
                <View>
                  <Text>{item.datos.owner}</Text>
                </View>
              </TouchableOpacity>
            )}
          />
        )}

        <FlatList
          data={filteredResults}
          keyExtractor={(user) => user.id}
          renderItem={({ item }) => (
            <TouchableOpacity onPress={() => this.handleUserSelect(item.id)}>
              <View>
                <Text>{item.datos.userName}</Text>
              </View>
            </TouchableOpacity>
          )}
        />

        <Text>Usuario seleccionado: {this.state.selectedUserId}</Text>
      </View>
    );
  }
}

const styles = StyleSheet.create({
  input: {
    height: 40,
    borderColor: "gray",
    borderWidth: 1,
    margin: 10,
    paddingLeft: 10,
  },
});

export default Search;
