import react, { Component } from "react";
import {
  TextInput,
  TouchableOpacity,
  View,
  Text,
  StyleSheet,
  FlatList 
} from "react-native";

import { auth, db } from "../../firebase/config";



class Profile extends Component {
  constructor() {
    super();
    this.state = {};
  }
componentDidMount(){
    //Traer datos
   db.collection('user').onSnapshot(
      data => {
          let info = [];

          data.forEach( unPost => {
              postsAMostrar.push(
                {
                  id: unPost.id,
                  datos: unPost.data()
                }
                )
            })

            this.setState({
                listaPost: postsAMostrar
            })
        }
    )
}


  logout() {
    auth.signOut();
    this.props.navigation.navigate("Login");
  }

  render() {
    console.log(this.state.users);
    return (
      <View>
        <Text>Profile</Text>

        <TouchableOpacity onPress={() => this.logout()}>
          <Text>Logout</Text>
        </TouchableOpacity>
      </View>
    );
  }
}

export default Profile;
