import React, { Component } from 'react';
import { View , Text, TouchableOpacity, StyleSheet } from 'react-native-web';
import {Camera} from 'expo-camera';

class MyCamera extends Component{
    constructor(props){
        super(props);
        this.state = {
            permission : false,
            photo: '',
            showCamera: true,
        }
    }
    componentDidMount(){
        Camera.requestCameraPermissionsAsync()
          .then((res)=>{
            if (res.granted == true)
               this.setState({
                   permission: true,
               })
          })
          .catch( e => console.log(e))          
      }

      takePicture(){
        this.metodosCamara.takePictureAsync()
        .then(photo => {
            this.setState({
                photo: photo.uri
            })
        })
        .catch(e => console.log(e))
      }
    render(){
        return(
            <View style={styles.formContainer}>
                {this.state.permission ? 
                    <View> 
                        <Camera style={styles.camera} type={Camera.Constants.Type.front} ref={metodosCamara => this.metodosCamara = metodosCamara}/>
                        <TouchableOpacity style={styles.button} onPress={()=> this.takePicture()}>
                                    <Text style={styles.textButton} > Take Picture</Text>    
                        </TouchableOpacity>
                    </View> : 
                <Text>No me diste los permisos de la c√°mara</Text>}
            </View>
        )
    }
}

const styles = StyleSheet.create({
    title:{
      fontWeight: 'bold'
    },

    camera: {
        flex:1,
        height:500
    },
    button: {
      flex:1,
      alignItems: 'center',
      backgroundColor: "blue",
      paddingHorizontal: 10,
      paddingVertical: 6,
      borderRadius: 4,
      borderWidth: 1,
      borderStyle: "solid",
      borderColor: "#28a745",
    },

    textButton: {
      color: "#fff",
    }
  });

export default MyCamera;
