import React, { Component } from "react";
import {
  TextInput,
  TouchableOpacity,
  View,
  Text,
  StyleSheet,
  FlatList 
} from "react-native";

import { auth, db } from "../../firebase/config";
import MyCamera from '../../componentes/MyCamera'



class Search extends Component {
  constructor() {
    super();
    this.state = { 
      search:'',
      results:''
    };
  }

  componentDidMount(){

  }


  render() {
    console.log(auth.currentUser);
    return (
      <View>
        <TextInput
        onChangeText={(text)=>this.setState({search: text})}
        placeholder='Search by username or email'
        keyboardType='default'
        value={this.state.busqueda}
        />
            {this.state.results.length === 0?
            (<Text>Couldn't find what you are searching for</Text>):
        (
    <FlatList
        data = {this.state.results}
        keyExtractor={user => user.id}
        renderItem = {({item}) => (
            <View>
            <Text>{item.datos.owner} - {item.datos.userName}</Text>
            </View>
        )} 
        />)}
        </View>



    );
  }
}
const styles = StyleSheet.create({
    container:{
      flex:1
    },
    formContainer: {
      paddingHorizontal: 10,
      marginTop: 20,
    },
    input: {
      height: 20,
      paddingVertical: 15,
      paddingHorizontal: 10,
      borderWidth: 1,
      borderColor: "#ccc",
      borderStyle: "solid",
      borderRadius: 6,
      marginVertical: 10,
    },
    button: {
      backgroundColor: "blue",
      paddingHorizontal: 10,
      paddingVertical: 6,
      textAlign: "center",
      borderRadius: 4,
      borderWidth: 1,
      borderStyle: "solid",
      borderColor: "#28a745",
    },
    textButton: {
      color: "#fff",
    },
  });

export default Search;
