import React, { Component } from 'react';
import {TextInput, TouchableOpacity, View, Text, StyleSheet, FlatList, Image} from 'react-native';
import { auth, db } from '../firebase/config';
import firebase from 'firebase';

import { FontAwesome } from '@expo/vector-icons';   
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHeart, faCoffee, faCheck } from '@fortawesome/free-solid-svg-icons';

class Post extends Component {
    constructor(props){
        super(props)
        this.state={
            like: false,
        }

        
    }

    componentDidMount(){
        let likes = this.props.infoPost.datos.likes

        if(likes.length === 0){
            this.setState({
                like: false
            })
        }
        if(likes.length > 0){
            likes.forEach( like => {if (like === auth.currentUser.email) {
                this.setState({ like: true })
            }})
        }
    }


   likear(){
    db.collection("posts").doc(this.props.infoPost.id).update({
        likes: firebase.firestore.FieldValue.arrayUnion(auth.currentUser.email)
    })
    .then(this.setState({like: true}))
   }

   dislike(){
    db.collection("posts").doc(this.props.infoPost.id).update({
        likes: firebase.firestore.FieldValue.arrayRemove(auth.currentUser.email)
    })
    .then(this.setState({like: false}))
   }
   comment(){
    db.collection("posts").doc(this.props.infoPost.id).update({
        comments: firebase.firestore.FieldValue.arrayUnion(auth.currentUser.email, )
    })
    .then(this.setState({like: false}))
   }


    render(){
        console.log(this.props.infoPost);
        return(
            <View style={styles.formContainer}>
             
                {/* PERFIL */}
                <TouchableOpacity
                    onPress={ ()=> this.props.navigation.navigate('Profile', this.props.infoPost.datos.owner ) }
                    style={styles.container}
                    >
                    {this.props.infoPost.datos.profilePic != '' ?
                    <Image 
                        style={styles.profilePic} 
                        source={{uri:this.props.infoPost.datos.profilePic}}
                        resizeMode='contain'/> :
                        <Image 
                        style={styles.profilePic} 
                        source={{uri:'https://cdn.pixabay.com/photo/2015/10/05/22/37/blank-profile-picture-973460_960_720.png'}}
                        resizeMode='contain'/> }

                    <Text style={styles.userName}>{this.props.infoPost.datos.userName}</Text>
                </TouchableOpacity>
                
                {/* FOTO DEL POSTEO */}
                <Image style={styles.camera} source={{uri:this.props.infoPost.datos.photo }}/>
                <View style={styles.contenedorLikes}>
                {this.state.like ? 
                <TouchableOpacity onPress={()=>this.dislike()}>

                    <FontAwesome style={styles.dislike}  name='heart' size={15}/>
                </TouchableOpacity>
                :
                <TouchableOpacity style={styles.like} onPress={()=>this.likear()} >

                   <FontAwesome name='heart-o' size={15}/>
                </TouchableOpacity>
                }

                {this.props.infoPost.datos.likes.length== 1? 
                  <Text style={styles.texto}>{this.props.infoPost.datos.likes.length} like</Text>
                  :
                  <Text style={styles.texto} >{this.props.infoPost.datos.likes.length} likes</Text>
                }
                </View>
                <View style={styles.description}>
                <TouchableOpacity
                    onPress={ ()=> this.props.navigation.navigate('Profile', this.props.infoPost.datos.owner)}>
                    <Text style={styles.nameDescription}>{this.props.infoPost.datos.userName} </Text>
                </TouchableOpacity>
                    <Text >{this.props.infoPost.datos.post}</Text>
                    
                </View>
                <TextInput
                        style={styles.inputComment}
                        onChangeText={(text) => this.setState({ post: text })}
                        placeholder="Comment..."
                        keyboardType="default"
                        value={this.state.post}
                    />
            </View>
        )
    }
}

const styles = StyleSheet.create({
    formContainer: {
      marginTop: 30,
      height: 500
      
    },
    dislike:{
        color: 'red'
    },
    contenedorLikes:{
        flex:1,
        flexDirection:'row',
        justifyContent: 'left',
        marginLeft: 10
    },
    container:{
        flex:1,
        flexDirection: 'row',
        height:50,
        width: '100vw',
        justifyContent:'left',
        marginLeft:5,
        marginTop:5,
        marginBottom:10,
    },
    profilePic:{
        height:40,
        width:40,
        borderWidth:2,
        borderRadius:25,
        borderColor:'white',
        marginRight:10
    },
    texto:{
        paddingLeft:10,
    },
    userName:{
        paddingTop:10,
        paddingLeft:6,
        fontWeight: 'bold',
    },
    camera: {
        width: "100vw",
        height: '70%',
        marginTop: 10,
        marginBottom:10
    },
    input: {
      height: 20,
      borderWidth: 1,
      borderColor: "#ccc",
      borderStyle: "solid",
      borderRadius: 6,
    },
    button: {
        flex:1,
      backgroundColor: "#E6DAE9",
      paddingHorizontal: 2,
      marginLeft:10,
      paddingVertical: 2,
      borderRadius: 4,
      borderWidth: 1,
      borderColor: 'white',
      borderStyle: "solid",
      width: 30,
      justifyContent:'center'
    },
    textButton: {
      color: "#fff",
    },
    image: {
        height: 400,
      },
      description:{
        flex:1,
        flexDirection:'row',
        justifyContent: 'left',
        marginLeft:10,
        marginBottom:10,
      },
      nameDescription:{
        fontWeight: 'bold',
      },
      inputComment: {
        height: 20,
        paddingVertical: 15,
        paddingHorizontal: 10,
        borderRadius: 6,
        marginVertical: 10,
        backgroundColor:'#eae0ed'},
      });



export default Post;
