import React, { Component } from "react";
import {
    TextInput,
    TouchableOpacity,
    View,
    Text,
    StyleSheet,
    FlatList,
    Image
} from "react-native";

import firebase from 'firebase';
import { Feather } from '@expo/vector-icons';
import { auth, db } from "../../firebase/config";
import { FontAwesome } from '@expo/vector-icons';


class EditProfile extends Component {
    constructor() {
        super();
        this.state = {
            commentedPic: null,
            userInfo: [],
            comment: ""
        };
    }

    componentDidMount() {
        db.collection('posts').where('photo', '==', this.props.route.params).onSnapshot(
            data => {
                let info = []
                data.forEach(i => {
                    info.push(
                        {
                            id: i.id,
                            datos: i.data()
                        })
                })

                this.setState({
                    commentedPic: info
                })
                    ;
            })

        db.collection('user')
            .where("owner", "==", auth.currentUser.email)
            .onSnapshot(
                data => {
                    let info = []
                    data.forEach(i => {
                        info.push({
                            id: i.id,
                            datos: i.data()
                        })
                    })

                    this.setState({
                        userInfo: info
                    })
                })
    }

    edit(texto) {
        db.collection("user").doc(this.state.commentedPic[0].id).update({
            comments: firebase.firestore.FieldValue.arrayUnion({
                owner: auth.currentUser.email,
                comment: texto,
                userName: this.state.userInfo[0].datos.userName,
                profilePic: this.state.userInfo[0].datos.profilePic,
                createdAt: Date.now()
            })
        })
            .then(this.setState({ comment: "" }))
    }

    render() {
        console.log(this.props);
        console.log('------');
        console.log(this.state.commentedPic);
        return (
            <View style={styles.formContainer}>
                <TouchableOpacity
                    onPress={() => this.props.navigation.navigate("Home")}
                    style={styles.coontainerFlecha}>
                    <FontAwesome style={styles.flecha} name="arrow-left" size='large' />
                </TouchableOpacity>
                {/* EMAIL */}
                <TextInput
                    style={styles.input}
                    onChangeText={(text) => this.setState({ email: text })}
                    placeholder={this.state.userInfo.owner}
                    keyboardType='email-address'
                    value={this.state.email}
                />

                {/* USER NAME */}
                <TextInput
                    style={styles.input}
                    onChangeText={(text) => this.setState({ userName: text })}
                    placeholder={this.state.userInfo.userName}
                    keyboardType='default'
                    value={this.state.userName}
                />

                {/* PASSWORD */}
                <TextInput
                    style={styles.input}
                    onChangeText={(text) => this.setState({ password: text })}
                    placeholder='Password'
                    keyboardType='default'
                    secureTextEntry={true}
                    value={this.state.password}
                />

                {/* MINI BIO */}
                <TextInput
                    style={styles.input}
                    onChangeText={(bio) => this.setState({ miniBio: bio })}
                    placeholder={this.state.userInfo.miniBio}
                    keyboardType='default'
                    value={this.state.miniBio}
                />

                {/* PROFILE PICTURE */}
                <TextInput
                    style={styles.input}
                    onChangeText={(url) => this.setState({ profilePic: url })}
                    placeholder='Add the URL of your picture'
                    keyboardType='default'
                    value={this.state.profilePic}
                />

                {this.state.email.length > 0 && this.state.password.length > 0 && this.state.userName.length > 0 ?

                    <TouchableOpacity style={styles.button} onPress={() =>
                        this.register(this.state.email, this.state.password, this.state.userName, this.state.miniBio, this.state.profilePic)}>

                        <Text style={styles.textButton} > Register</Text>

                    </TouchableOpacity> :

                    <TouchableOpacity style={styles.buttonError} onPress={() => this.setState({ textError: 'You must complete the required fields' })}>
                        <Text style={styles.textButton} > Register</Text>
                    </TouchableOpacity>}

                {this.state.textError.length > 0 ? <Text style={styles.textError}> {this.state.textError} </Text> : false}
                <TouchableOpacity onPress={() => this.props.navigation.navigate('Login')}>
                    <Text style={styles.register}> You already have an account? Login</Text>
                </TouchableOpacity>
            </View>
        );
    }
}


const styles = StyleSheet.create({
    container: {
        height: '100%',
        backgroundColor: 'white',
    },
    title: {
        fontWeight: "bold",
        fontSize: 20
    },
    user: {
        flexDirection: 'row',
        height: 50,
        width: '100vw',
        justifyContent: 'left',
        marginLeft: 5,
        marginTop: 5,
        marginBottom: 10,
        alignItems: 'center'
    },
    profilePic: {
        height: 40,
        width: 40,
        borderWidth: 2,
        borderRadius: 25,
        borderColor: 'white',
        marginRight: 10
    },
    titleContainer: {
        flexDirection: 'row',
        height: 50,
        width: '100%',
        justifyContent: 'left',
        marginLeft: 5,
        marginBottom: 10,
        alignItems: 'center',
        backgroundColor: '#F4F4F3'
    },
    description: {
        flex: 1,
        flexDirection: 'row',
        marginLeft: 10,
        marginBottom: 1,
        marginTop: 1
    },
    nameDescription: {
        fontWeight: 'bold',
    },
    comment: {
        padding: 2
    },
    coontainerFlecha: {
        marginLeft: 20,
        marginRight: 20
    },
    containerComment: {
        flexDirection: "row",
        alignItems: 'center',
        width: '100%',
        alignContent: "flex-end",
        backgroundColor: '#eae0ed',
        borderRadius: 10,
    },
    inputComment: {
        width: '90%',
        justifyContent: 'space-around',
        height: 20,
        paddingVertical: 15,
        paddingHorizontal: 10,
        borderRadius: 6,
        marginVertical: 10,

    }
});

export default EditProfile;
