import React, { Component } from "react";
import {
  TextInput,
  TouchableOpacity,
  View,
  Text,
  StyleSheet,
  FlatList 
} from "react-native";

import { auth, db } from "../../firebase/config";



class ProfileUsers extends Component {
  constructor(props) {
    super(props);
    this.state = {
      userInfo: []
    };
  }
componentDidMount(){
    //Traer datos
    console.log(this.props);
    db.collection('user').where('owner','==', this.props.rote.params).onSnapshot(
       data => {
           let info = []
           data.forEach( i => {
               info.push(
                 {
                   id: i.id,
                   datos: i.data()
                 })
             })

             this.setState({
                 userInfo: info
             })
       }
     )
}

  render() {
    return (
       <View>
        {/* {<FlatList 
            data= {this.state.userInfo}
            keyExtractor={ i  => i.id }
            renderItem={ ({item}) => 
            {
              return (
              <React.Fragment>
                <Text> { item.datos.userName } </Text>
                <Text> { item.datos.owner } </Text>
                <Text> { item.datos.miniBio } </Text>

              </React.Fragment>
              )
            } 
           }
        />} */}
  <Text>PROFILE USERS</Text>

        {/*  {item.datos.owner == auth.currentUser.email ? <Text> { item.datos.miniBio } </Text> : <Text>Hola</Text>} */}

           {/*{if (item.datos.owner == auth.currentUser.email) {
            return (<Text> { item.datos.miniBio } </Text>)
          }}  */}

        {/*{auth.currentUser.email == this.state.userInfo.datos.owner ?
        <Text>
          {this.state.userinfo.datos}
        </Text> : false} */}
      </View>
    );
  }
}

export default ProfileUsers;
