import React, { Component } from "react";
import {
  TextInput,
  TouchableOpacity,
  View,
  Text,
  StyleSheet,
  FlatList 
} from "react-native";

import { auth, db } from "../../firebase/config";




class Search extends Component {
  constructor() {
    super();
    this.state = { 
      search:'',
      results:''
    };
  }

  componentDidMount(){
    /* PRIMERO BUSCO POR EMAIL */
    db.collection('users').where('owner', '==', this.state.search).get()
    .then(findOwner => {
        /* AHORA BUSCO POR NOMBRE DE USUARIO */
        db.collection('users').where('userName', '==', this.state.busqueda).get()
            .then(findUserName => {
                const users = [];
                findOwner.forEach(user => {
                    users.push({
                        id: user.id,
                        datos: user.data()
                    });
                });
                resultUserName.forEach(doc => {
                    users.push({
                        id: doc.id,
                        datos: doc.data()
                    });
                });

                this.setState({
                    resultados: users
                });
            })
            .catch(error => {
                console.error(error);
            });
    })
    .catch(error => {
        console.error(error);
    });

  }


  render() {
    console.log(auth.currentUser);
    return (
        <View>

            <TextInput
            onChangeText={(text)=>this.setState({search: text})}
            placeholder='Search by username or email'
            keyboardType='default'
            value={this.state.search}
            />
            {this.state.results.length === 0
            ?
            (<Text>Couldn't find what you are searching for</Text>)
            :

            (<FlatList
                data = {this.state.results}
                keyExtractor={user => user.id}
                renderItem = {({item}) => (
                    <View>
                    <Text>{item.datos.owner} - {item.datos.userName}</Text>
                    </View>
                )} 
            />)}

        </View>



    );
  }
}
const styles = StyleSheet.create({
    container:{
      flex:1
    },
    formContainer: {
      paddingHorizontal: 10,
      marginTop: 20,
    },
    input: {
      height: 20,
      paddingVertical: 15,
      paddingHorizontal: 10,
      borderWidth: 1,
      borderColor: "#ccc",
      borderStyle: "solid",
      borderRadius: 6,
      marginVertical: 10,
    },
    button: {
      backgroundColor: "blue",
      paddingHorizontal: 10,
      paddingVertical: 6,
      textAlign: "center",
      borderRadius: 4,
      borderWidth: 1,
      borderStyle: "solid",
      borderColor: "#28a745",
    },
    textButton: {
      color: "#fff",
    },
  });

export default Search;
