import React, { Component } from "react";
import { View, Text, FlatList, Image, StyleSheet, TouchableOpacity } from "react-native";
import { db, auth } from "../../firebase/config";

class Profile extends Component {
  constructor(props) {
    super(props);
    this.state = {
      userInfo: [],
      userPosts: [],
    };
  }

  componentDidMount() {
    /* BUSCO LOS USUARIOS */
    db.collection("user").onSnapshot(
      (data) => {
        let info = [];
        data.forEach((i) => {
          info.push({
            id: i.id,
            datos: i.data(),
          });
        });

        this.setState({
          userInfo: info,
        });
      }
    );

    /* BUSCO LOS POSTEOS */
    db.collection("posts")
      .where("owner", "==", auth.currentUser.email)
      .onSnapshot((data) => {
        let info = [];
        data.forEach((i) => {
          info.push({
            id: i.id,
            datos: i.data(),
          });
        });

        this.setState({
          userPosts: info,
        });
      });
  }

  logout() {
    auth.signOut();
    this.props.navigation.navigate("Login");
  }

  deletePost(id) {
    db.collection("posts")
      .doc(id)
      .delete()
      .then(() => {
        console.log("Post eliminado");
      })
      .catch((error) => {
        console.log(error);
      });
  }

  render() {
    return (
      <View style={styles.formContainer}>
        <FlatList
          data={this.state.userInfo}
          keyExtractor={(i) => i.id}
          renderItem={({ item }) => {
            if (item.datos.owner === auth.currentUser.email) {
              return (
                <View style={styles.containerDatos}>
                  <Text>{item.datos.userName}</Text>
                  <Text>{item.datos.owner}</Text>
                  {item.datos.miniBio.length > 0 ? <Text>{item.datos.miniBio}</Text> : false}
                  <Text>{this.state.userPosts.length} posts</Text>
                </View>
              );
            }
          }}
        />

        <FlatList
          data={this.state.userPosts}
          keyExtractor={(i) => i.id}
          renderItem={({ item }) => (
            <View style={styles.containerPost}>
              <Image style={styles.camera} source={{ uri: item.datos.photo }} />
              <Text style={styles.textoPost}>{item.datos.post}</Text>
              {item.datos.likes.length === 1 ? (
                <Text style={styles.textoPost}>{item.datos.likes.length} like</Text>
              ) : (
                <Text style={styles.textoPost}>{item.datos.likes.length} likes</Text>
              )}
              <TouchableOpacity onPress={() => this.deletePost(item.id)}>
                <Text>Delete post</Text>
              </TouchableOpacity>
            </View>
          )}
        />

        <TouchableOpacity onPress={() => this.logout()}>
          <Text>Logout</Text>
        </TouchableOpacity>
      </View>
    );
  }
}

const styles = StyleSheet.create({
  formContainer: {
    height: "100%",
    marginBottom: 10,
  },
  containerDatos: {
    alignItems: "center",
    height: "100%",
    marginBottom: 5,
  },
  containerPost: {
    marginTop: 5,
    marginBottom: 5,
    height: "70%",
  },
  camera: {
    width: "100%",
    height: "50%",
    marginTop: 10,
    marginBottom: 15,
  },
  textoPost: {
    marginLeft: 5,
  },
});

export default Profile;
