import React from "react";
import { TextInput, View, Text, FlatList, TouchableOpacity, StyleSheet } from "react-native";
import { useNavigation } from '@react-navigation/native';
import { auth, db } from "../../firebase/config";

const Search = () => {
  const navigation = useNavigation();
  const [search, setSearch] = React.useState("");
  const [results, setResults] = React.useState([]);
  const [selectedUserId, setSelectedUserId] = React.useState("");

  React.useEffect(() => {
    const unsubscribe = db.collection("user").onSnapshot((snapshot) => {
      let info = [];
      snapshot.forEach((doc) => {
        info.push({
          id: doc.id,
          datos: doc.data(),
        });
      });

      setResults(info);
    });

    return () => unsubscribe();
  }, []);

  const handleUserSelect = (selectedUserId) => {
    // Redirigir a la pantalla de ProfileUsers con el ID del usuario seleccionado
    navigation.navigate('ProfileUsers', { userId: selectedUserId });
  };

  // Filtrar usuarios basados en la entrada del usuario
  const filteredResults = results.filter((user) =>
    user.datos.userName.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <View>
      <TextInput
        placeholder="Search by username"
        keyboardType="default"
        value={search}
        style={styles.input}
        onChangeText={(text) => setSearch(text)}
      />

      <FlatList
        data={filteredResults}
        keyExtractor={(user) => user.id}
        renderItem={({ item }) => (
          <TouchableOpacity onPress={() => handleUserSelect(item.id)}>
            
              <Text>{item.datos.userName}</Text>
            
          </TouchableOpacity>
        )}
      />

      <Text>Usuario seleccionado: {selectedUserId}</Text>
    </View>
  );
};

const styles = StyleSheet.create({
  input: {
    height: 40,
    borderColor: "gray",
    borderWidth: 1,
    margin: 10,
    paddingLeft: 10,
  },
});

export default Search;
