import React, { Component } from 'react';
import { auth, db } from '../../firebase/config';
import {TextInput, TouchableOpacity, View, Text, StyleSheet} from 'react-native';

import MyCamera from '../../componentes/MyCamera'

class Register extends Component {
    constructor(){
        super()
        this.state={
            showCamera:false,
            email:'',
            userName:'',
            password:'',
            miniBio:'',
            profilePic: '',
            textError: false,
            url: ''
        }
    }

    register (email, pass, userName , miniBio , profilePic){
        /* ERRORES */
        if(this.state.email == '' || this.state.email.includes("@") == false){
            return this.setState({textError: "You must enter a valid email address"})
        }else if (this.state.password == '' || this.state.password.length <6){
            return this.setState({textError: "Your password must be at least 6 characters long"})
        }else if (this.state.userName == '') {
            return this.setState({textError:'You must complete the username'})
        }

        auth.createUserWithEmailAndPassword(email, pass)
            .then( response => {
                console.log(response);
                db.collection("user").add({
                    owner: email,
                    createdAt: Date.now(),
                    userName: userName,
                    miniBio: miniBio,
                    profilePic: profilePic
                })
                this.props.navigation.navigate("Login")
            })
            .catch((error) => {
                this.setState({
                  textError: error.message
              })
                console.log(error);
              });

    }

    onImageUpload(url){
        this.setState({ url: url , showCamera: false});
      }

    render(){
        console.log(this.state)

        return(

            <View style={styles.formContainer}>
                {this.state.showCamera
                ?
                <MyCamera onImageUpload={(url) => this.onImageUpload(url)} />    
            :
            <>

            </>
            }


            </View>
        )
    }
}

const styles = StyleSheet.create({
    title:{
        fontWeight: 'bold'
      },
    formContainer:{
        paddingHorizontal:10,
        marginTop: 20,
    },
    input:{
        height:20,
        paddingVertical:15,
        paddingHorizontal: 10,
        borderWidth:1,
        borderColor: '#ccc',
        borderStyle: 'solid',
        borderRadius: 6,
        marginVertical:10,
    },
    button:{
        flex:1,
        alignItems: 'center',
        backgroundColor:'#28a745',
        paddingHorizontal: 10,
        paddingVertical: 6,
        textAlign: 'center',
        borderRadius:4, 
        borderWidth:1,
        borderStyle: 'solid',
        borderColor: '#28a745'
    },
    buttonError:{
        flex:1,
        alignItems: 'center',
        backgroundColor:'grey',
        paddingHorizontal: 10,
        paddingVertical: 6,
        textAlign: 'center',
        borderRadius:4, 
        borderWidth:1,
        borderStyle: 'solid',
        borderColor: 'white',
        color: 'white'
    },
    textButton:{
        color: '#fff'
    },
    textError:{
        color:'red'
    }

})


export default Register;
