import React, { Component } from "react";
import {
  TextInput,
  TouchableOpacity,
  View,
  Text,
  StyleSheet,
  FlatList,
  Image,
  ActivityIndicator
} from "react-native";

import { auth, db } from "../../firebase/config";



class Profile extends Component {
  constructor(props) {
    super(props);
    this.state = {
      userInfo: [],
      userPosts: []
    };
  }
  componentDidMount() {
    /* BUSCO LOS USUARIOS */
    db.collection('user').where("owner", "==", auth.currentUser.email).onSnapshot(
      data => {
        let info = []
        data.forEach(i => {
          info.push(
            {
              id: i.id,
              datos: i.data()
            })
        })

        this.setState({
          userInfo: info
    }, ()=> console.log(this.state.userInfo))
      }
    )

    /* BUSCO LOS POSTEOS */
    db.collection('posts').where('owner', '==', auth.currentUser.email).onSnapshot(
      data => {
        let info = []
        data.forEach(i => {
          info.push(
            {
              id: i.id,
              datos: i.data()
            })
        })

        this.setState({
          userPosts: info
        })
          ;
      }
    )
  }


  logout() {
    auth.signOut();
    this.props.navigation.navigate("Login");
  }

  deletePost(id) {
    db.collection('posts').doc(id).delete()
      .then(() => {
        console.log("Post eliminado")
      })
      .catch((error) => {
        console.log(error)
      })
  }

  render() {

    return (

      <View style={styles.formContainer}>


        {this.state.userInfo.length > 0 ?
        <>
          <View style= {styles.conteinerProfile}>

            <Image 
            style={styles.profilePic} 
            source={{uri:this.state.userInfo[0].datos.profilePic}}
            resizeMode='contain'/> 

            <View style={styles.containerDatos}>
            <Text style={styles.userName}> {this.state.userInfo[0].datos.userName} </Text>
            <Text> {this.state.userInfo[0].datos.owner} </Text>
            {this.state.userInfo[0].datos.miniBio.length > 0 ? <Text> {this.state.userInfo[0].datos.miniBio} </Text> : false}
            <Text> { this.state.userPosts.length } posts</Text>
            </View>
            <TouchableOpacity onPress={() => this.logout()}>
            <Text>Logout</Text>
          </TouchableOpacity>
          </View>

          <View style={styles.container}>

            {<FlatList
              data={this.state.userPosts}
              keyExtractor={i => i.id}
              renderItem={({ item }) => {
                return (

                  <View style={styles.containerPost}>

                          {/* PERFIL */}
                      <TouchableOpacity
                          onPress={ ()=> this.props.navigation.navigate('Profile', this.props.infoPost.datos.owner ) }
                          style={styles.container}
                          >
                          {this.props.infoPost.datos.profilePic != '' ?
                          <Image 
                              style={styles.profilePic} 
                              source={{uri:this.props.infoPost.datos.profilePic}}
                              resizeMode='contain'/> :
                              <Image 
                              style={styles.profilePic} 
                              source={{uri:'https://cdn.pixabay.com/photo/2015/10/05/22/37/blank-profile-picture-973460_960_720.png'}}
                              resizeMode='contain'/> }

                          <Text style={styles.userName}>{this.props.infoPost.datos.userName}</Text>
                      </TouchableOpacity>

                      {/* FOTO DEL POSTEO */}
                      <Image style={styles.camera} source={{uri:this.props.infoPost.datos.photo }}/>
                      <View style={styles.contenedorLikes}>
                      {this.state.like ? 
                      <TouchableOpacity onPress={()=>this.dislike()}>

                          <FontAwesome style={styles.dislike}  name='heart' size={15}/>
                      </TouchableOpacity>
                      :
                      <TouchableOpacity style={styles.like} onPress={()=>this.likear()} >

                        <FontAwesome name='heart-o' size={15}/>
                      </TouchableOpacity>
                      }

                      {this.props.infoPost.datos.likes.length== 1? 
                        <Text style={styles.texto}>{this.props.infoPost.datos.likes.length} like</Text>
                        :
                        <Text style={styles.texto} >{this.props.infoPost.datos.likes.length} likes</Text>
                      }
                      </View>
                      <View style={styles.description}>
                      <TouchableOpacity
                          onPress={ ()=> this.props.navigation.navigate('Profile', this.props.infoPost.datos.owner)}>
                          <Text style={styles.nameDescription}>{this.props.infoPost.datos.userName} </Text>
                      </TouchableOpacity>
                          <Text >{this.props.infoPost.datos.post}</Text>
                      </View>


















                    <TouchableOpacity onPress={() => this.deletePost(item.id)}>
                      <Text>Delete post</Text>
                    </TouchableOpacity>

                  </View>)
              }}
              />}

          </View>


          </>
          : 
          <View style={styles.activityIndicatorContainer}>
            <ActivityIndicator  size='small' color='purple' />
          </View>
        }



      </View>
    );
  }
}

const styles = StyleSheet.create({
  activityIndicatorContainer:{
    flex:1,
    justifyContent:'center',
    alignItems:'center',
  },
  container:{
    flex: 1,
    height:'100%'
  },
  userName:{
    fontWeight: 'bold'
  },
  formContainer: {
    height: '100vh',
    marginBottom: 10,
    marginTop:20,
  },
  containerDatos:{
    height: '100%',
    marginBottom: 5,
  },
  conteinerProfile: {
    flexDirection:'row',
    justifyContent: 'center',
    marginBottom: 5,
    marginTop:15,
  },
  containerPost: {
    marginTop: 5,
    marginBottom: 5,
    height: '70%'
  },
  camera: {
    width: "100vw",
    height: '50vh',
    marginTop: 10,
    marginBottom: 15
  },
  textoPost: {
    marginLeft: 5,
  },
  profilePic:{
    height:70,
    width:70,
    borderRadius:45,
    marginRight:10
},
});

export default Profile;
