import React, { Component } from "react";
import {
    TextInput,
    TouchableOpacity,
    View,
    Text,
    StyleSheet,
    FlatList,
    Image
} from "react-native";

import firebase from 'firebase';
import { Feather } from '@expo/vector-icons';
import { auth, db } from "../../firebase/config";
import { FontAwesome } from '@expo/vector-icons';


class EditProfile extends Component {
    constructor() {
        super();
        this.state = {
            commentedPic: null,
            userInfo: [],
        };
    }

    componentDidMount() {
        db.collection('posts').where('photo', '==', this.props.route.params).onSnapshot(
            data => {
                let info = []
                data.forEach(i => {
                    info.push(
                        {
                            id: i.id,
                            datos: i.data()
                        })
                })

                this.setState({
                    commentedPic: info
                })
                    ;
            })

        db.collection('user')
            .where("owner", "==", auth.currentUser.email)
            .onSnapshot(
                data => {
                    let info = []
                    data.forEach(i => {
                        info.push({
                            id: i.id,
                            datos: i.data()
                        })
                    })

                    this.setState({
                        userInfo: info
                    })
                })
    }

    render() {
        return (
            <View style={styles.formContainer}>
                <TouchableOpacity
                    onPress={() => this.props.navigation.navigate("Home")}
                    style={styles.coontainerFlecha}>
                    <FontAwesome style={styles.flecha} name="arrow-left" size='large' />
                </TouchableOpacity>
                {/* EMAIL */}
                <TextInput
                    style={styles.input}
                    onChangeText={(text) => this.setState({ email: text })}
                    placeholder={this.state.userInfo.owner}
                    keyboardType='email-address'
                    value={this.state.userInfo.owner}
                />

                {/* USER NAME */}
                <TextInput
                    style={styles.input}
                    onChangeText={(text) => this.setState({ userName: text })}
                    placeholder={this.state.userInfo.userName}
                    keyboardType='default'
                    value={this.state.userInfo.userName}
                />

                {/* MINI BIO */}
                <TextInput
                    style={styles.input}
                    onChangeText={(bio) => this.setState({ miniBio: bio })}
                    placeholder={this.state.userInfo.miniBio}
                    keyboardType='default'
                    value={this.state.miniBio}
                />

                {/* PROFILE PICTURE */}
                <TextInput
                    style={styles.input}
                    onChangeText={(url) => this.setState({ profilePic: url })}
                    placeholder='Add the URL of your profile picture'
                    keyboardType='default'
                    value={this.state.profilePic}
                />

            </View>
        );
    }
}
const styles = StyleSheet.create({
    title:{
        fontWeight: 'bold',
        color: '#896a92',
        marginTop:10,
      },
    formContainer:{
        paddingHorizontal:10,
        marginTop: 20,
    },
    input:{
        backgroundColor:'#eae0ed',
        height: 20,
        paddingVertical: 15,
        paddingHorizontal: 10,
        borderRadius: 6,
        marginVertical: 10,
    },
    button:{
        flex:1,
        alignItems: 'center',
        backgroundColor: '#6c4e75',
        paddingHorizontal: 10,
        paddingVertical: 6,
        borderRadius: 4,
    },
    buttonError:{
        flex:1,
        alignItems: 'center',
        backgroundColor:'#896a92',
        paddingHorizontal: 10,
        paddingVertical: 6,
        textAlign: 'center',
        borderRadius:4, 
        color: 'white'
    },
    textButton:{
        color: '#fff'
    },
    textError:{
        color:'red'
    },
    register:{
        marginTop: 10
      },
      buttonPhoto:{
        flex:1,
        alignItems: 'center',
        backgroundColor:'#C1C1C1',
        paddingHorizontal: 10,
        paddingVertical: 6,
        textAlign: 'center',
        borderRadius:4, 
        marginBottom: 10,
      }

})

export default EditProfile;
