import react, { Component } from 'react';
import { auth, db } from '../../firebase/config';
import {TextInput, TouchableOpacity, View, Text, StyleSheet} from 'react-native';

class Register extends Component {
    constructor(props){
        super()
        this.state={
            email:'',
            userName:'',
            password:'',
            miniBio:'',
            profilePic: '',
            textError: false
        }
    }

    register (email, pass, userName , miniBio , profilePic){
        auth.createUserWithEmailAndPassword(email, pass)
            .then( response => {
                db.collection("user").add({
                    owner: email,
                    createdAt: Date.now(),
                    userName: userName,
                    miniBio: miniBio,
                    profilePic: profilePic
                })
            .then(
                this.props.navigation.navigate("Login")
            )
            .catch(error => console.log(error))

            })
            .catch( error => {
                this.setState({
                    textError: error.message
                })

                console.log(error);

            })
    }


    render(){
        return(
            <View style={styles.formContainer}>
                <Text>Register</Text>

                {/* EMAIL */}
                <TextInput
                    style={styles.input}
                    onChangeText={(text)=>this.setState({email: text})}
                    placeholder='* example@email.com'
                    keyboardType='email-address'
                    value={this.state.email}
                    />

                {/* USER NAME */}
                <TextInput
                    style={styles.input}
                    onChangeText={(text)=>this.setState({userName: text})}
                    placeholder='* User name'
                    keyboardType='default'
                    value={this.state.userName}
                    />

                {/* PASSWORD */}
                <TextInput
                    style={styles.input}
                    onChangeText={(text)=>this.setState({password: text})}
                    placeholder='* Password'
                    keyboardType='default'
                    secureTextEntry={true}
                    value={this.state.password}
                />

                {/* MINI BIO */}
                <TextInput
                    style={styles.input}
                    onChangeText={(bio)=>this.setState({miniBio: bio})}
                    placeholder='Tell us something about yourself'
                    keyboardType='default'
                    value={this.state.miniBio}
                    />

                {/* PROFILE PICTURE */}

                {this.state.email.length > 1 && this.state.password.length >1 && this.state.userName.length > 1 ? 

                <TouchableOpacity style={styles.button} onPress={()=> 
                this.register(this.state.email, this.state.password, this.state.userName , this.state.miniBio , this.state.profilePic)}>

                    <Text style={styles.textButton} > Register</Text>    

                </TouchableOpacity> : 

                <TouchableOpacity style={styles.buttonError} onPress={()=> this.setState({textError: 'You must complete the required fields'})}>
                    <Text style={styles.textButton} > Register</Text>    
                </TouchableOpacity> }

                {this.state.textError.length > 0 ? <Text style={styles.textError}> {this.state.textError} </Text> : false }
                <TouchableOpacity onPress={ () => this.props.navigation.navigate('Login')}>
                   <Text> You already have an account? Login</Text>
                </TouchableOpacity>

            </View>
        )
    }
}

const styles = StyleSheet.create({
    formContainer:{
        paddingHorizontal:10,
        marginTop: 20,
    },
    input:{
        height:20,
        paddingVertical:15,
        paddingHorizontal: 10,
        borderWidth:1,
        borderColor: '#ccc',
        borderStyle: 'solid',
        borderRadius: 6,
        marginVertical:10,
    },
    button:{
        flex:1,
        alignItems: 'center',
        backgroundColor:'#28a745',
        paddingHorizontal: 10,
        paddingVertical: 6,
        textAlign: 'center',
        borderRadius:4, 
        borderWidth:1,
        borderStyle: 'solid',
        borderColor: '#28a745'
    },
    buttonError:{
        flex:1,
        alignItems: 'center',
        backgroundColor:'grey',
        paddingHorizontal: 10,
        paddingVertical: 6,
        textAlign: 'center',
        borderRadius:4, 
        borderWidth:1,
        borderStyle: 'solid',
        borderColor: 'white',
        color: 'white'
    },
    textButton:{
        color: '#fff'
    },
    textError:{
        color:'red'
    }

})


export default Register;
