import React, { Component } from "react";
import { TextInput, View, Text, FlatList, TouchableOpacity, StyleSheet } from "react-native";
import { auth, db } from "../../firebase/config";

class Search extends Component {
  constructor() {
    super();
    this.state = {
      search: "",
      results: [],
      selectedUserId: "",
    };
  }

  componentDidMount() {
    db.collection("user").onSnapshot((snapshot) => {
      let info = [];
      snapshot.forEach((doc) => {
        info.push({
          id: doc.id,
          datos: doc.data(),
        });
      });

      this.setState({
        results: info,
      });
    });
  }

  handleUserSelect(selectedUserId) {
    // Cargar el usuario seleccionado
    this.setState({
      selectedUserId,
    });
  }

  render() {
    const { search, results } = this.state;

    // Filtrar usuarios basados en la entrada del usuario
    const filteredResults = results.filter((user) =>
      user.datos.userName.toLowerCase().includes(search.toLowerCase())
    );

    return (
      <View>
        <TextInput
          placeholder="Search by username"
          keyboardType="default"
          value={search}
          style={styles.input}
          onChangeText={(text) => this.setState({ search: text })}
        />

        <FlatList
          data={filteredResults}
          keyExtractor={(user) => user.id}
          renderItem={({ item }) => (
            <TouchableOpacity onPress={() => this.handleUserSelect(item.id)}>
              <View>
                <Text>{item.datos.userName}</Text>
              </View>
            </TouchableOpacity>
          )}
        />

        <Text>Usuario seleccionado: {this.state.selectedUserId}</Text>
      </View>
    );
  }
}

const styles = StyleSheet.create({
  input: {
    height: 40,
    borderColor: "gray",
    borderWidth: 1,
    margin: 10,
    paddingLeft: 10,
  },
});

export default Search;
