import React, { Component } from "react";
import {
  TextInput,
  TouchableOpacity,
  View,
  Text,
  StyleSheet,
  FlatList 
} from "react-native";

import { auth, db } from "../../firebase/config";




class Search extends Component {
  constructor() {
    super();
    this.state = { 
      search:'',
      results:''
    };
  }

  componentDidMount(){

  }
  search(){
        /* PRIMERO BUSCO POR EMAIL */
        db.collection('user').where('owner', '==', this.state.search).get()
        .then(resultOwner => {
            /* AHORA BUSCO POR NOMBRE DE USUARIO */
            db.collection('user').where('userName', '==', this.state.search).get()
                .then(resultUserName => {
                    const resultUsers = [];
                    resultOwner.forEach(user => {
                        resultUsers.push({
                            id: user.id,
                            datos: user.data()
                        });
                    });
                    resultUserName.forEach(doc => {
                        resultUsers.push({
                            id: doc.id,
                            datos: doc.data()
                        });
                    });

                    this.setState({
                        results: resultUsers
                    });
                })
                .catch(e => {
                    console.error(e);
                });
        })
        .catch(e => {
            console.error(e);
        });
  }

  render() {
    console.log(auth.currentUser);
    return (
        <View>

            <TextInput
            onChangeText={(text)=>this.setState({search: text})}
            placeholder='Search by username or email'
            keyboardType='default'
            value={this.state.search}
            style={styles.input}
            />
            <TouchableOpacity style={styles.buttonError} onPress={()=> this.setState({textError: 'You must complete the required fields'})}>
            <Text style={styles.textButton} > Login</Text>    
        </TouchableOpacity>
            {this.state.results.length === 0
            ?
            (<Text>Couldn't find what you are searching for</Text>)
            :

            (<FlatList
                data = {this.state.results}
                keyExtractor={user => user.id}
                renderItem = {({item}) => (
                    <View>
                    <Text>{item.datos.owner} - {item.datos.userName}</Text>
                    </View>
                )} 
            />)}

        </View>



    );
  }
}
const styles = StyleSheet.create({
    title:{
        fontWeight: 'bold'
      },
    formContainer:{
        paddingHorizontal:10,
        marginTop: 20,
    },
    input:{
        height:20,
        paddingVertical:15,
        paddingHorizontal: 10,
        borderWidth:1,
        borderColor: '#ccc',
        borderStyle: 'solid',
        borderRadius: 6,
        marginVertical:10,
    },
    button:{
        flex:1,
        alignItems: 'center',
        backgroundColor:'#28a745',
        paddingHorizontal: 10,
        paddingVertical: 6,
        textAlign: 'center',
        borderRadius:4, 
        borderWidth:1,
        borderStyle: 'solid',
        borderColor: '#28a745'
    },
    buttonError:{
        flex:1,
        alignItems: 'center',
        backgroundColor:'grey',
        paddingHorizontal: 10,
        paddingVertical: 6,
        textAlign: 'center',
        borderRadius:4, 
        borderWidth:1,
        borderStyle: 'solid',
        borderColor: 'white',
        color: 'white'
    },
    textButton:{
        color: '#fff'
    },
    textError:{
        color:'red'
    }

})

export default Search;
