  }
}

const styles = StyleSheet.create({
  /* container:{
    flex:1
  }, */
  profilePic:{
    height:40,
    width:40,
    borderRadius:25,
    borderColor:'white',
    marginRight:10
},
  containerDatos:{
    alignItems:'center',
    height: 130,
    marginBottom:5,

  },
  containerPost: {
    flex:1,
    flexDirection:'row',
    flexWrap: 'wrap',
